mapper.select:
select
  id, parent_id, role_name, sort, role_alias, is_deleted
from rd_role


        and id = #{id}


        and parent_id = #{parentId}


        and role_name = #{roleName}


        and sort = #{sort}


        and role_alias = #{roleAlias}


        and is_deleted = #{isDeleted}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from rd_role


        and id = #{id}


        and parent_id = #{parentId}


        and role_name = #{roleName}


        and sort = #{sort}


        and role_alias = #{roleAlias}


        and is_deleted = #{isDeleted}
